import React, { useState, useEffect } from "react";

import { ContactForm } from "../../components/contactForm/ContactForm";
import { TileList } from "../../components/tileList/TileList";


export const ContactsPage = ({ contacts }) => {
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [duplicate, setDuplicate] = useState(false);
  const [allContacts, setAllContacts] = useState([]);

  // Check for duplicate names when contacts or name change
  useEffect(() => {
    if (contacts.some(contact => contact.name === name)){
    setDuplicate(true);
    }
  }, [contacts, name]);

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (!duplicate) {
      // Add contact info and clear data
      const newContact = { name: name, phone: phone, email: email };
      setAllContacts([...allContacts, newContact]);
      setName("");
      setPhone("");
      setEmail("");
    }
  };

  return (
    <div>
      <section>
        <h2>Add Contact</h2>
        <form onSubmit={handleSubmit}>
          <label>
            Name:
            <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
          </label>
          <br />
          <label>
            Phone:
            <input type="text" value={phone} onChange={(e) => setPhone(e.target.value)} />
          </label>
          <br />
          <label>
            Email:
            <input type="text" value={email} onChange={(e) => setEmail(e.target.value)} />
          </label>
          <br />
          <button type="submit">Add Contact</button>
          {duplicate && <p style={{ color: 'red' }}>Duplicate contact name!</p>}
        </form>
      </section>
      <hr />
      <section>
        <h2>Contacts</h2>
        <ul>
          {allContacts.map((contact, index) => (
            <li key={index}>{contact.name} - {contact.phone} - {contact.email}</li>
          ))}
        </ul>
      </section>
    </div>
  );
};
